export default {
  name: "NumberButton",
  props: ["name", "timeInterval", "keyCodeDict"],
  computed: {
    keyCode: function keyCode() {
      var parseCode = parseInt(this.name);

      if (parseCode !== NaN) {
        return [parseCode + 48, parseCode + 96];
      } else {
        return this.keyCodeDict[this.name];
      }
    }
  },
  mounted: function mounted() {
    var self = this;
    document.addEventListener("keydown", function () {
      var keyCode = window.event.keyCode;

      if (self.keyCode.indexOf(keyCode) != -1) {
        self.$el.setAttribute("class", "active");
        self.$el.click();
        setTimeout(function () {
          return self.$el.removeAttribute("class");
        }, self.timeInterval);
      }
    });
  },
  methods: {
    handleKeyUp: function handleKeyUp(event) {
      alert(event);
    }
  }
};
